query ArticlesByObsession($after: String = "", $perPage: Int = 10, $slug: [String]!) {
  obsessions(where: {slug: $slug}) {
    __typename
    nodes {
      __typename
      ...ObsessionParts
      posts: content(after: $after, first: $perPage) {
        __typename
        nodes {
          __typename
          ... on Post {
            ...ArticleTeaserParts
          }
          ... on Bulletin {
            ...BulletinParts
          }
          ... on Promotion {
