query NugsByTag($perPage: Int!, $tag: [String]!) {
  nugs(first: $perPage, where: {tagSlugIn: $tag}) {
    __typename
    nodes {
      __typename
      ...NugParts
    }
  }
}
fragment NugParts on Nug {
  __typename
  id
  blocks {
    __typename
    ...BlockParts
    connections {
      __typename
      ... on Post {
        ...ArticleListItemParts
      }
      ... on MediaItem {
        ...MediaParts
      }
    }
  }
  dateGmt
  emailLists {
    __typename
    edges {
      __typename
      node {
        __typename
        emailListId
        listId
        name
      }
    }
  }
  link
  modifiedGmt
  nugId
  slug
  title
}
fragment BlockParts on Block {
  __typename
  attributes {
    __typename
    name
    value
  }
  id
  innerHtml
  tagName
  type
}
fragment ArticleListItemParts on Post {
  __typename
  ...ArticleTeaserParts
  editions {
    __typename
    edges {
      __typename
      node {
        __typename
        name
        slug
      }
    }
  }
}
fragment ArticleTeaserParts on Post {
  __typename
  bulletin {
    __typename
    campaign {
      __typename
      id
      logo
      name
      slug
    }
    sponsor {
      __typename
      attribution
      id
      slug
      name
    }
    clientTracking {
      __typename
      article
      elsewhere
      logo
    }
  }
  dateGmt
  featuredImage {
    __typename
    ...MediaParts
  }
  video {
    __typename
    ...VideoParts
  }
  id
  kicker
  link
  postId
  title
  video {
    __typename
    ...VideoParts
  }
  trailerVideo {
    __typename
    ...VideoParts
  }
}
fragment MediaParts on MediaItem {
  __typename
  altText
  caption
  credit
  id
  mediaDetails {
    __typename
    height
    width
  }
  sourceUrl
  title
}
fragment VideoParts on VideoData {
  __typename
  id
  duration
  episode
  playlistId
  season
  type
}
